import React, { Component } from "react";

class Header extends Component {
  state = {
    class: "noScroll p-0 py-1 w-100 position-fixed z_index_3"
  };
  componentDidMount = () => {
    window.addEventListener("scroll", this.handleScroll, { passive: true });
  };

  componentWillUnmount = () => {
    window.removeEventListener("scroll", this.handleScroll);
  };

  handleScroll = event => {
    if (window.scrollY) {
      this.setState({
        class: "Scroll p-0 py-1 w-100 position-fixed z_index_3"
      });
    } else {
      this.setState({
        class: "noScroll p-0 py-1 w-100 position-fixed z_index_3"
      });
    }
  };
  render() {
    return (
      <header className={this.state.class}>
        <nav className="container navbar w-100 navbar-expand-lg p-0">
          <a className="navbar-brand" href="/">
            <img src="./images/logo/logo.png" className="w-75" alt="Logo" />
          </a>
          <button
            className="navbar-toggler"
            type="button"
            data-toggle="collapse"
            data-target="#navbarNav"
            aria-controls="navbarNav"
            aria-expanded="false"
            aria-label="Toggle navigation"
          >
            <span className="navbar-toggler-icon" />
          </button>
          <div className="collapse navbar-collapse" id="navbarNav">
            <ul className="navbar-nav mx-auto text-uppercase">
              <li className="nav-item mr-3 active">
                <a className="nav-link" href="/">
                  Home <span className="sr-only">(current)</span>
                </a>
              </li>
              <li className="nav-item mr-3">
                <a className="nav-link" href="/">
                  Menu
                </a>
              </li>
              <li className="nav-item mr-3">
                <a className="nav-link" href="/">
                  Reservation
                </a>
              </li>
              <li className="nav-item mr-3">
                <a className="nav-link" href="/">
                  Blog
                </a>
              </li>
              <li className="nav-item mr-3">
                <a className="nav-link" href="/">
                  About
                </a>
              </li>
              <li className="nav-item">
                <a className="nav-link" href="/">
                  Contact
                </a>
              </li>
            </ul>
            <ul className="navbar-nav head_size_fonts">
              <li className="nav-item">
                <a className="nav-link" href="/">
                  <i className="fa fa-free-code-camp" />
                </a>
              </li>
              <li className="nav-item mx-3">
                <a className="nav-link" href="/">
                  <i className="fa fa-search" />
                </a>
              </li>
              <li className="nav-item">
                <a className="nav-link" href="/">
                  <i className="fa fa-align-justify" />
                </a>
              </li>
            </ul>
          </div>
        </nav>
      </header>
    );
  }
}

export default Header;
